sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Search for exisitng ticket associated with open tracker record
    var vehService = new GlideRecord('x_1022286_lva_vehicle_servicing');
    //Create a GlideRecord to query servicing table
    vehService.addQuery('u_reference_1', current.sys_id);
    //Add a query condition to findd rrecords where 'vehicle_tracker' matches the current record's sys_id.
    vehService.addQuery('tracker_status', 'open');
    //Add a query condition to find records with 'tracker_status' set to 'open'.
    vehService.query();
    //Execute query

    if (!vehService.next()) {
        //If no matching record was found in the previous query:

        //Create a new ticket record.
        var createticket = new GlideRecord('x_1022286_lva_vehicle_servicing');
        //Create a new Gliderecord object for servicing table

        //Get the current date set as the service ticket's opening date
        var gd = new GlideDate();

        //populate required fields for new ticket
        createticket.initialize();
        //Initialize new record.
        createticket.setValue('u_reference_1', current.sys_id);
        //Set 'vehicle_tracker' field with the current record's sys_id.
        createticket.setValue('vehicle', current.vehicle);
        // Set the 'vehicle'field with the current record's 'vehicle' value
        createticket.setValue('vehicle_status', 'in service');
        //Set 'vehicle_status' to 'in service'.
        createticket.setValue('date_sent_for_servicing', gd.getValue());
        // Set 'date_sent_for_servicing' to current date.
        createticket.setValue('ticket_status', 'open');
        //Set 'ticket_status' to 'open'.
        createticket.insert();
        // Insert new record to the table.
    }
})(current, previous);
